const InscripcionProyecto = ({ idProyecto, estado, inscripciones }) => {
  const [estadoInscripcion, setEstadoInscripcion] = useState("");
  const [crearRegistro, { data, loading, error }] =
    useMutation(CREAR_INSCRIPCION);
  //const {userData}= useUser();

  useEffect(() => {
    // if (userData && inscripciones) {
    if (inscripciones) {
      const flt = inscripciones.filter(
        (el) => el.estudiante._id === "61af74d5ba5adc3b57f4b11f"
      );
      if (flt.length > 0) {
        setEstadoInscripcion(flt[0].estado);
      }
    }
  }, [inscripciones]);

  useEffect(() => {
    if (data) {
      console.log("datos de la inscripcion", data);
      // Swal({
      //     title: "Gestión de Proyectos",
      //     text: "Inscripción Exitosa",
      //     icon: "success",
      //     timer: "1000"
      // });
      toast.success("Registro Exitoso");
    }
  }, [data]);

  const confirmarInscripcion = () => {
    crearRegistro({
      variables: {
        proyecto: idProyecto,
        estudiante: "61af74d5ba5adc3b57f4b11f",
      },
    });
    //crearInscripcion({ variables: { proyecto: idProyecto, estudiante: userData._id } });
  };

  return (
    <>
      {estadoInscripcion !== "" ? (
        <span>
          Ya estas inscrito en este proyecto y el estado es {estadoInscripcion}
        </span>
      ) : (
        <ButtonLoading
          onClick={() => confirmarInscripcion()}
          disabled={estado === "INACTIVO"}
          loading={loading}
          text="Inscribirme en este proyecto"
        />
      )}
    </>
  );
};

