import React from "react";
import { useMutation, useQuery } from '@apollo/client';
import { Link } from "react-router-dom";
import Input from 'components/Input';
import { GET_AVANCES } from "graphql/avances/queries";

const Avances = () => {
    const { dataAvances: queryDataAvances, errorAvances: queryErrorAvances, loadingAvances: queryLoadingAvances, } = useQuery(GET_AVANCES) 
  return (
    <div className="p-10 flex flex-col items-center">
      <div className="self-start">
        <Link to="/proyectos">
          <i className="fas fa-arrow-left" />
        </Link>
      </div>
      <h1 className="text-2xl font-bold text-gray-900">Crear Nuevo Avance</h1>
      <form>
        <Input
          name="proyecto"
          label="Nombre del proyecto"
          required={true}
          type="text"
        />
        <Input
          name="fecha"
          label="Fecha de actualización"
          required={true}
          type="date"
        />
        <Input
          name="descripcion"
          label="Descripción del avance"
          required={true}
          type="text"
        />
        <Input
          name="creadoPor"
          label="Creado por:"
          defaultValue="Laura"
          readOnly="readOnly"
          type="text"
        />
      </form>
    </div>
  );
};

export default Avances;
