import React, { useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import{ Link } from "react-router-dom";
import Input from "components/Input";
import { GET_PROYECTO } from "graphql/proyectos/queries";
import ReactLoading from 'react-loading';
import ButtonLoading from 'components/ButtonLoading';
import { toast } from "react-toastify";
import { useParams } from "react-router";

const Avances = () => {
  const {data: project, error, loading} = useQuery(GET_PROYECTO, {variables:{ _id }});
  const { _id } = useParams();

  useEffect(() => {
    console.log("data servidor", project);
  }, [project]);

  useEffect(() => {
    if (error) {
      console.error(`error obteniendo los datos ${error}`);
      toast.error("Error consultando los datos");
    }
  }, [error]);

  const fechaActual = () => {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth()+1; //January is 0!
    var yyyy = today.getFullYear();
    return dd+"/"+mm+"/"+yyyy;
  }

  if (loading) return <ReactLoading type='cylon' color='#4c2882' height={667} width={365} />;

  return (
    <div className="p-10 flex flex-col items-center">
      <div className="self-start">
        <Link to="/proyectos">
          <i className="fas fa-arrow-left" />
        </Link>
      </div>
      <h1 className="text-2xl font-bold text-gray-900">Crear Nuevo Avance</h1>
      <form onSubmit={submitForm} className='flex flex-col items-center justify-center min-w-min w-1/3 mx-auto bg-gray-100  py-3 text-center text-xl text-gray-500 uppercase font-bold h-full rounded-3xl'>
        <label >
          Nombre del proyecto
        </label>
        <p>{project.nombre}</p>        
        <Input
          name="fecha"
          label="Fecha de actualización"
          required={true}
          type="date"
          defaultValue = {fechaActual()}
        />
        <Input
          name="descripcion"
          label="Descripción del avance"
          required={true}
          type="text"
        />
       
        <ButtonLoading text='Crear Proyecto' loading={false} disabled={false} />
      </form>
    </div>
  );
};

export default Avances;
